mapper.select:
SELECT * FROM devops_cd_audit_record
WHERE user_id=#{userId}
AND status='not_audit'

    AND project_id IN

        #{projectId}

mapper.update:
UPDATE devops_cd_audit_record
SET project_id =#{projectId}
WHERE stage_record_id=#{stageRecordId}

UPDATE devops_cd_audit_record
SET project_id =#{projectId}
WHERE job_record_id=#{jobRecordId}
